// Lê o binário do nó anterior (ajuste o nome do nó se necessário)
const binary = $input.first().binary.data;

// Converte de base64 para buffer para calcular o tamanho real em bytes
const buffer = Buffer.from(binary.data, "base64");
const fileSize = buffer.length;

return [
  {
    json: {
      with_preview: true,
      billable: true,
      file_name: binary.fileName,
      file_size: fileSize,
      mime_type: binary.mimeType
    }
  }
];

// Pega o binário do nó anterior (ajuste o nome se necessário)
const inputBinary = $("Download file").item.binary.data;

// Converte de base64 para buffer
const buffer = Buffer.from(inputBinary.data, "base64");

// Tamanho máximo da parte (ajuste conforme retorno da API Kommo)
const maxPartSize = 524288; // 512 KB

// Cria os chunks
const chunks = [];
for (let i = 0; i < buffer.length; i += maxPartSize) {
  const end = Math.min(i + maxPartSize, buffer.length);
  const chunk = buffer.slice(i, end);
  chunks.push(chunk);
}

// Retorna cada chunk como item binário separado
return chunks.map((chunk, index) => {
  return {
    json: { part: index + 1 },
    binary: {
      data: {
        data: chunk.toString("base64"),
        mimeType: inputBinary.mimeType,
        fileName: `${inputBinary.fileName || "file"}_part${index + 1}`,
      },
    },
  };
});

